mapper.select:
select * from (select a.*,rowNum rn from ( select P.p_no as p_no, P.cate_no as cate_no, P.s_no as s_no, P.p_group_buying as p_group_buying, P.p_follow_sale as p_follow_sale, P.p_name as p_name, P.p_detail as p_detail, P.p_group_price as p_group_price, P.p_sell_price as p_sell_price, P.p_follow_price as p_follow_price, P.p_stock as p_stock, P.p_regdate as p_regdate, P.p_status as p_status, P.p_hit as p_hit, C.cate_large as cate_large, C.cate_small as cate_small, PI.p_img as p_img, S.s_name as s_name from product P left outer join category C on P.cate_no = C.cate_no left outer join product_img PI on P.p_no = PI.p_no and PI.p_img_order=1 left outer join shop S on P.s_no = S.s_no
${search} like '%'||#{keyword}||'%' and p_status=#{status}
${search} like '%'||#{keyword}||'%' and p_status='1'
order by p_no desc) a ) where rn between #{startRow} and #{endRow}

select count(*) from product P left outer join category C on P.cate_no = C.cate_no left outer join product_img PI on P.p_no = PI.p_no and PI.p_img_order=1 left outer join shop S on P.s_no = S.s_no
${search} like '%'||#{keyword}||'%' and p_status=#{status}
${search} like '%'||#{keyword}||'%' and p_status='1'

mapper.update:
update product set cate_no=#{cate_no}, s_no=#{s_no}, p_group_buying=#{p_group_buying},
	p_follow_sale=#{p_follow_sale}, p_name=#{p_name}, p_detail=#{p_detail},
	p_group_price=#{p_group_price}, p_sell_price=#{p_sell_price}, p_follow_price=#{p_follow_price},
	p_stock=#{p_stock} 
	where p_no=#{p_no}
